package io.github.juniqlim.realworld;

import io.github.juniqlim.realworld.user.domain.User;
import io.github.juniqlim.realworld.user.repository.UserRepository;
import java.security.KeyFactory;
import java.security.NoSuchAlgorithmException;
import java.security.PrivateKey;
import java.security.PublicKey;
import java.security.spec.InvalidKeySpecException;
import java.security.spec.PKCS8EncodedKeySpec;
import java.security.spec.X509EncodedKeySpec;
import java.util.Base64;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.CommandLineRunner;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.context.annotation.Bean;
import org.springframework.stereotype.Component;

@Component
class MyCLRunner implements CommandLineRunner {
	@Autowired TestRepository testRepository;

	@Override
	public void run(String... args) {
		UserRepository userRepository = testRepository.userRepository();
		User juniq = userRepository.findByUsername("juniq");
		System.out.println("juniq = " + juniq.token());
	}
}

@SpringBootApplication
public class RealworldSpringbootDeclarativeOopStyleApplication {
	public static void main(String[] args) {
		SpringApplication.run(RealworldSpringbootDeclarativeOopStyleApplication.class, args);
	}

	@Bean
	public PrivateKey privateKey() throws NoSuchAlgorithmException, InvalidKeySpecException {
		String testPrivateKey = "";
		return KeyFactory.getInstance("RSA")
				.generatePrivate(new PKCS8EncodedKeySpec(Base64.getDecoder().decode(testPrivateKey)));
	}

	@Bean
	public PublicKey publicKey() throws NoSuchAlgorithmException, InvalidKeySpecException {
		String stringPublicKey = "";
		return KeyFactory.getInstance("RSA").generatePublic(new X509EncodedKeySpec(Base64.getDecoder().decode(stringPublicKey)));
	}
}
